def start
  puts "Appuyez sur entrée pour commencer"
  f_input = gets
  return f_input
end

def continue
  puts "Appuyez sur entrée pour passer à la question suivante"
  sec_input = gets
  return sec_input
end

def how_many(journalists)
  puts "Combien y a-t-il des journalistes dans cet array ?"
  number = journalists.length
  puts "Il y a #{number} journalistes dans l'array"
  return number
end

def min_journalist(journalists)
  puts "Quel est le handle le plus court de cette liste ?"
  # shortest_handle = journalists.min{|word,worrd| word.size <=> worrd.size}
  shortest_handle = journalists.min_by { |word| word.length}
  puts " le handle le plus court de cette liste est : #{shortest_handle}"
  return shortest_handle
end

def count_journalist(journalists)
  puts "Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
  number = journalists.count {|name| name.delete("@").length == 5}
  puts "Il y a #{number} handle contenant 5 caractères (sans @)"
  return number
end

def upper_name(journalists)
  puts "Combien commencent par une majuscule (première lettre juste après le @) ?"
  number = journalists.count {|letter| letter[1].match?(/[[:upper:]]/)}
  puts " Il y a #{number} handle qui commencent pas une majuscule"
  return number
end

def alpha_order(journalists)
  puts "Trie la liste de handle par ordre alphabétique."
  order = journalists.sort.inspect
  puts "Voici la liste des handle par ordre alphabétique : #{order}"
  return order
end

def sort_journalist(journalists)
  puts "Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
  sorting = journalists.sort_by {|s| s.size}
  puts "Voici la liste des handle par taille"
  puts sorting.inspect
  return sorting
end

def which_position(journalists)
  puts "Quelle est la position dans l'array de la personne @epenser ?"
  position = journalists.index("@epenser")
  puts " @epenser est à la position : #{position}"
  return position
end

def list_nb(journalists)
  puts "Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
# définition du nb d'itérations pour la boucle for
  letter_min = journalists.min_by{|value| value.length}.length
  letter_max = journalists.max_by{|value| value.length}.length

# création du hash
  element_size = {}

# boucle for où letter_nb(itération) -- je veux incrémenter dans mon hash le nb de lettres -1 (moins le @) = éléments du tableau où nb lettres correspond aux éléments ! En gros le nombre de lettres est représenté par les itérations (key) et les valeurs sont le nb de mots qui ont ce nb de lettres
  for letter_nb in (letter_min..letter_max) do
    element_size[letter_nb - 1] = journalists.count{|element| letter_nb == element.length}
  end
  puts "Voici la répartition des handle par nombre de lettres composant leur handle"

  puts element_size.inspect
  return element_size
end

