  upcase = upper_name(JOURNALISTS)
  sec_input = continue
  sorting_alpha = alpha_order(JOURNALISTS)
  sec_input = continue
  sorting_size = sort_journalist(JOURNALISTS)
  sec_input = continue
  epenser = which_position(JOURNALISTS)
  sec_input = continue
  hash_journalists = list_nb(JOURNALISTS)
 end

perform
